import React, { Component } from "react";
import "./App.css";
import Egg from "./Egg";

class App extends Component {
  state = {
    nrOfCrackedEggs: 0,
    highscore: 0
  };

  increateCount = () => {
    const updatedNumber = this.state.nrOfCrackedEggs + 1;
    const { highscore } = this.state;
    this.setState({
      nrOfCrackedEggs: this.state.nrOfCrackedEggs + 1,
      highscore:
        updatedNumber > this.state.highscore ? updatedNumber : highscore
    });
  };

  restart = () => {
    this.setState({
      nrOfCrackedEggs: 0
    });
  };

  render() {
    const eggs = [];

    for (let i = 0; i < 10; i++) {
      eggs.push(<Egg increateCount={this.increateCount} />);
    }

    return (
      <div className="app">
        <h1 class="title">Happy easter!</h1>

        <div class="eggs">{eggs}</div>
        <div class="amount">
          <div>{this.state.nrOfCrackedEggs}</div>
          <div class="highscore">Highscore: {this.state.highscore}</div>
        </div>
      </div>
    );
  }
}

export default App;
